function palindrome(str) {
  let firstStr = str
 firstStr =firstStr.split('')
 firstStr =firstStr.map((character)=>{
    if(character.toLowerCase() != character.toUpperCase()|| !isNaN(parseInt(character, 10))){
      return character.toLowerCase()
    }
  })
  let secondStr = firstStr
  firstStr = firstStr.join('')
 secondStr =secondStr.reverse()
 secondStr =secondStr.join('')
  console.log(firstStr)
  console.log(secondStr)
  if(firstStr===secondStr){
    return true
  }else{
    return false
  }
}



